file = open('/home/giang/Documents/MEP_PSO/data/data_200_2.txt')
# doc file
data = file.read()
lines = data.split('\n')

N = int(lines[0])
print(N)

circle = []
for i in range(1, N+1):
    line = lines[i]
    parts = line.split()
    x = float(parts[0])
    y = float(parts[1])
    r = float(parts[2])
    # print(x)
    print('Sensor ',i, ': x = ', x, ', y = ', y, ', r = ',r)
    circle1 = plt.Circle((x, y), r, color='r')
    ax = plt.gca()
    ax.add_artist(circle1)

plt.show()

file.close()